import readline from 'readline';
import { TodoList } from './todoList.js';

const rl = readline.createInterface({
  input: process.stdin,
  output: process.stdout
});

const todoList = new TodoList();

function showMenu() {
  console.log('\n=== TO-DO LIST ===');
  console.log('1. Add task');
  console.log('2. View all tasks');
  console.log('3. Mark task complete');
  console.log('4. Delete task');
  console.log('5. Exit');
  console.log('==================\n');
}

function prompt(question) {
  return new Promise(resolve => rl.question(question, resolve));
}

async function main() {
  console.log('Welcome to your TO-DO List!\n');

  let running = true;
  while (running) {
    showMenu();
    const choice = await prompt('Choose an option: ');

    switch (choice.trim()) {
      case '1': {
        const task = await prompt('Enter a new task: ');
        if (task.trim()) {
          todoList.addTask(task.trim());
          console.log('✓ Task added!');
        } else {
          console.log('✗ Task cannot be empty.');
        }
        break;
      }

      case '2': {
        const tasks = todoList.getAllTasks();
        if (tasks.length === 0) {
          console.log('No tasks yet!');
        } else {
          console.log('\nYour tasks:');
          tasks.forEach((task, index) => {
            const status = task.completed ? '✓' : '○';
            console.log(`  ${index + 1}. [${status}] ${task.text}`);
          });
          console.log('');
        }
        break;
      }

      case '3': {
        const tasks = todoList.getAllTasks();
        if (tasks.length === 0) {
          console.log('No tasks to complete!');
        } else {
          console.log('\nYour tasks:');
          tasks.forEach((task, index) => {
            const status = task.completed ? '✓' : '○';
            console.log(`  ${index + 1}. [${status}] ${task.text}`);
          });
          const indexStr = await prompt('Enter task number to mark complete: ');
          const index = parseInt(indexStr) - 1;
          if (index >= 0 && index < tasks.length) {
            todoList.completeTask(index);
            console.log('✓ Task marked complete!');
          } else {
            console.log('✗ Invalid task number.');
          }
        }
        break;
      }

      case '4': {
        const tasks = todoList.getAllTasks();
        if (tasks.length === 0) {
          console.log('No tasks to delete!');
        } else {
          console.log('\nYour tasks:');
          tasks.forEach((task, index) => {
            const status = task.completed ? '✓' : '○';
            console.log(`  ${index + 1}. [${status}] ${task.text}`);
          });
          const indexStr = await prompt('Enter task number to delete: ');
          const index = parseInt(indexStr) - 1;
          if (index >= 0 && index < tasks.length) {
            todoList.deleteTask(index);
            console.log('✓ Task deleted!');
          } else {
            console.log('✗ Invalid task number.');
          }
        }
        break;
      }

      case '5': {
        console.log('Goodbye!');
        running = false;
        break;
      }

      default:
        console.log('✗ Invalid option. Please choose 1-5.');
    }
  }

  rl.close();
}

main().catch(console.error);

export class TodoList {
  constructor() {
    this.tasks = [];
    this.nextId = 1;
  }

  addTask(text) {
    const task = {
      id: this.nextId++,
      text,
      completed: false,
      createdAt: new Date()
    };
    this.tasks.push(task);
    return task;
  }

  getAllTasks() {
    return this.tasks;
  }

  getTask(id) {
    return this.tasks.find(task => task.id === id);
  }

  completeTask(index) {
    if (index >= 0 && index < this.tasks.length) {
      this.tasks[index].completed = true;
      return this.tasks[index];
    }
    return null;
  }

  deleteTask(index) {
    if (index >= 0 && index < this.tasks.length) {
      return this.tasks.splice(index, 1)[0];
    }
    return null;
  }

  clearCompleted() {
    this.tasks = this.tasks.filter(task => !task.completed);
    return this.tasks;
  }

  getStats() {
    const total = this.tasks.length;
    const completed = this.tasks.filter(t => t.completed).length;
    const pending = total - completed;
    return { total, completed, pending };
  }
}

import { test } from 'node:test';
import assert from 'node:assert';
import { TodoList } from './todoList.js';

test('TodoList - add task', () => {
  const list = new TodoList();
  const task = list.addTask('Test task');
  assert.strictEqual(task.text, 'Test task');
  assert.strictEqual(task.completed, false);
  assert.strictEqual(list.getAllTasks().length, 1);
});

test('TodoList - complete task', () => {
  const list = new TodoList();
  list.addTask('Task 1');
  list.completeTask(0);
  assert.strictEqual(list.getAllTasks()[0].completed, true);
});

test('TodoList - delete task', () => {
  const list = new TodoList();
  list.addTask('Task 1');
  list.addTask('Task 2');
  list.deleteTask(0);
  assert.strictEqual(list.getAllTasks().length, 1);
  assert.strictEqual(list.getAllTasks()[0].text, 'Task 2');
});

test('TodoList - get stats', () => {
  const list = new TodoList();
  list.addTask('Task 1');
  list.addTask('Task 2');
  list.addTask('Task 3');
  list.completeTask(0);
  list.completeTask(1);

  const stats = list.getStats();
  assert.strictEqual(stats.total, 3);
  assert.strictEqual(stats.completed, 2);
  assert.strictEqual(stats.pending, 1);
});

test('TodoList - clear completed', () => {
  const list = new TodoList();
  list.addTask('Task 1');
  list.addTask('Task 2');
  list.completeTask(0);
  list.clearCompleted();

  assert.strictEqual(list.getAllTasks().length, 1);
  assert.strictEqual(list.getAllTasks()[0].text, 'Task 2');
});

