import fs from 'fs/promises';
import path from 'path';
import { fileURLToPath } from 'url';

const __dirname = path.dirname(fileURLToPath(import.meta.url));
const DATA_FILE = path.join(__dirname, '../data/tasks.json');

async function ensureDataDir() {
  const dataDir = path.dirname(DATA_FILE);
  try {
    await fs.mkdir(dataDir, { recursive: true });
  } catch (err) {
    // Directory already exists or error creating it
  }
}

export async function loadTasks() {
  try {
    await ensureDataDir();
    const data = await fs.readFile(DATA_FILE, 'utf-8');
    return JSON.parse(data);
  } catch (err) {
    // File doesn't exist or is invalid, return empty array
    return [];
  }
}

export async function saveTasks(tasks) {
  try {
    await ensureDataDir();
    await fs.writeFile(DATA_FILE, JSON.stringify(tasks, null, 2), 'utf-8');
  } catch (err) {
    console.error('Error saving tasks:', err);
    throw err;
  }
}

import express from 'express';
import cors from 'cors';
import path from 'path';
import { fileURLToPath } from 'url';
import { TodoList } from './todoList.js';
import { loadTasks, saveTasks } from './storage.js';

const __dirname = path.dirname(fileURLToPath(import.meta.url));
const app = express();
const PORT = process.env.PORT || 3000;

// Middleware
app.use(cors());
app.use(express.json());
app.use(express.static(path.join(__dirname, '../public')));

// Initialize TodoList
const todoList = new TodoList();

// Load tasks on startup
async function initializeTasks() {
  try {
    const tasks = await loadTasks();
    if (tasks.length > 0) {
      // Restore tasks from storage
      todoList.tasks = tasks;
      // Update nextId to avoid conflicts
      const maxId = Math.max(...tasks.map(t => t.id), 0);
      todoList.nextId = maxId + 1;
    }
  } catch (err) {
    console.error('Error loading tasks:', err);
  }
}

// API Routes

// GET all tasks
app.get('/api/tasks', (req, res) => {
  res.json(todoList.getAllTasks());
});

// POST new task
app.post('/api/tasks', (req, res) => {
  const { text } = req.body;

  if (!text || !text.trim()) {
    return res.status(400).json({ error: 'Task text cannot be empty' });
  }

  const task = todoList.addTask(text.trim());
  saveTasks(todoList.getAllTasks()).catch(err => console.error('Save error:', err));

  res.status(201).json(task);
});

// GET single task
app.get('/api/tasks/:id', (req, res) => {
  const { id } = req.params;
  const task = todoList.getTask(parseInt(id));

  if (!task) {
    return res.status(404).json({ error: 'Task not found' });
  }

  res.json(task);
});

// PATCH task (mark complete)
app.patch('/api/tasks/:id', (req, res) => {
  const { id } = req.params;
  const { completed } = req.body;

  const task = todoList.getTask(parseInt(id));
  if (!task) {
    return res.status(404).json({ error: 'Task not found' });
  }

  if (completed !== undefined) {
    task.completed = completed;
  }

  saveTasks(todoList.getAllTasks()).catch(err => console.error('Save error:', err));
  res.json(task);
});

// DELETE task
app.delete('/api/tasks/:id', (req, res) => {
  const { id } = req.params;
  const taskId = parseInt(id);
  const task = todoList.getTask(taskId);

  if (!task) {
    return res.status(404).json({ error: 'Task not found' });
  }

  todoList.tasks = todoList.tasks.filter(t => t.id !== taskId);
  saveTasks(todoList.getAllTasks()).catch(err => console.error('Save error:', err));

  res.json({ message: 'Task deleted', task });
});

// GET stats
app.get('/api/stats', (req, res) => {
  res.json(todoList.getStats());
});

// DELETE all completed tasks
app.delete('/api/tasks/completed/all', (req, res) => {
  const remaining = todoList.clearCompleted();
  saveTasks(todoList.getAllTasks()).catch(err => console.error('Save error:', err));

  res.json({ message: 'Completed tasks cleared', remaining });
});

// Root route
app.get('/', (req, res) => {
  res.sendFile(path.join(__dirname, '../public/index.html'));
});

// Error handling
app.use((err, req, res, next) => {
  console.error(err);
  res.status(500).json({ error: 'Internal server error' });
});

// Start server
app.listen(PORT, async () => {
  await initializeTasks();
  console.log(`🚀 Server running on http://localhost:${PORT}`);
  console.log(`📋 Visit http://localhost:${PORT} to use the app`);
});

