// API Configuration
const API_BASE = '/api';

// DOM Elements
const taskForm = document.getElementById('taskForm');
const taskInput = document.getElementById('taskInput');
const tasksList = document.getElementById('tasksList');
const totalCount = document.getElementById('totalCount');
const completedCount = document.getElementById('completedCount');
const pendingCount = document.getElementById('pendingCount');
const clearCompletedBtn = document.getElementById('clearCompletedBtn');

// Event Listeners
taskForm.addEventListener('submit', handleAddTask);
clearCompletedBtn.addEventListener('click', handleClearCompleted);

// Initialize
loadTasks();

// Functions
async function loadTasks() {
  try {
    const response = await fetch(`${API_BASE}/tasks`);
    if (!response.ok) throw new Error('Failed to load tasks');

    const tasks = await response.json();
    renderTasks(tasks);
    updateStats();
  } catch (error) {
    console.error('Error loading tasks:', error);
    showError('Failed to load tasks');
  }
}

async function handleAddTask(e) {
  e.preventDefault();

  const text = taskInput.value.trim();
  if (!text) {
    showError('Task cannot be empty');
    return;
  }

  try {
    const response = await fetch(`${API_BASE}/tasks`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ text })
    });

    if (!response.ok) {
      const error = await response.json();
      throw new Error(error.error || 'Failed to add task');
    }

    taskInput.value = '';
    taskInput.focus();
    loadTasks();
    showSuccess('Task added!');
  } catch (error) {
    console.error('Error adding task:', error);
    showError(error.message);
  }
}

async function handleToggleTask(id, currentStatus) {
  try {
    const response = await fetch(`${API_BASE}/tasks/${id}`, {
      method: 'PATCH',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ completed: !currentStatus })
    });

    if (!response.ok) throw new Error('Failed to update task');

    loadTasks();
  } catch (error) {
    console.error('Error updating task:', error);
    showError('Failed to update task');
  }
}

async function handleDeleteTask(id) {
  if (!confirm('Are you sure you want to delete this task?')) {
    return;
  }

  try {
    const response = await fetch(`${API_BASE}/tasks/${id}`, {
      method: 'DELETE'
    });

    if (!response.ok) throw new Error('Failed to delete task');

    loadTasks();
    showSuccess('Task deleted!');
  } catch (error) {
    console.error('Error deleting task:', error);
    showError('Failed to delete task');
  }
}

async function handleClearCompleted() {
  if (!confirm('Delete all completed tasks?')) {
    return;
  }

  try {
    const response = await fetch(`${API_BASE}/tasks/completed/all`, {
      method: 'DELETE'
    });

    if (!response.ok) throw new Error('Failed to clear completed tasks');

    loadTasks();
    showSuccess('Completed tasks cleared!');
  } catch (error) {
    console.error('Error clearing completed tasks:', error);
    showError('Failed to clear completed tasks');
  }
}

function renderTasks(tasks) {
  if (!tasks || tasks.length === 0) {
    tasksList.innerHTML = '<p class="empty-state">No tasks yet. Add one to get started!</p>';
    return;
  }

  tasksList.innerHTML = tasks.map(task => `
    <div class="task-item ${task.completed ? 'completed' : ''}">
      <input 
        type="checkbox" 
        class="task-checkbox" 
        ${task.completed ? 'checked' : ''}
        onchange="handleToggleTask(${task.id}, ${task.completed})"
      >
      <div class="task-content">
        <div class="task-text">${escapeHtml(task.text)}</div>
        <div class="task-meta">${formatDate(task.createdAt)}</div>
      </div>
      <div class="task-actions">
        <button 
          class="btn btn-danger" 
          onclick="handleDeleteTask(${task.id})"
        >
          Delete
        </button>
      </div>
    </div>
  `).join('');
}

async function updateStats() {
  try {
    const response = await fetch(`${API_BASE}/stats`);
    if (!response.ok) throw new Error('Failed to load stats');

    const stats = await response.json();
    totalCount.textContent = stats.total;
    completedCount.textContent = stats.completed;
    pendingCount.textContent = stats.pending;
  } catch (error) {
    console.error('Error loading stats:', error);
  }
}

function escapeHtml(text) {
  const div = document.createElement('div');
  div.textContent = text;
  return div.innerHTML;
}

function formatDate(dateString) {
  const date = new Date(dateString);
  return date.toLocaleDateString('en-US', {
    year: 'numeric',
    month: 'short',
    day: 'numeric',
    hour: '2-digit',
    minute: '2-digit'
  });
}

function showSuccess(message) {
  showNotification(message, 'success');
}

function showError(message) {
  showNotification(message, 'error');
}

function showNotification(message, type) {
  // Create a simple notification (you could enhance this)
  const notification = document.createElement('div');
  notification.style.cssText = `
    position: fixed;
    top: 20px;
    right: 20px;
    padding: 1rem 1.5rem;
    background-color: ${type === 'success' ? '#10b981' : '#ef4444'};
    color: white;
    border-radius: 0.5rem;
    box-shadow: 0 10px 15px rgba(0, 0, 0, 0.1);
    z-index: 1000;
    animation: slideIn 0.3s ease;
  `;
  notification.textContent = message;

  document.body.appendChild(notification);

  setTimeout(() => {
    notification.style.animation = 'slideOut 0.3s ease';
    setTimeout(() => notification.remove(), 300);
  }, 3000);
}

// Add slideOut animation to styles
const style = document.createElement('style');
style.textContent = `
  @keyframes slideOut {
    from {
      opacity: 1;
      transform: translateX(0);
    }
    to {
      opacity: 0;
      transform: translateX(400px);
    }
  }
`;
document.head.appendChild(style);

